    /**
     * 
     * @param evt 
     */
    private void jTablaCursosMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jTablaCursosMouseClicked
        String salida = "Nombre:  " + jTablaCursos.getValueAt(jTablaCursos.getSelectedRow(), 0) + "\n"
                + "Siglas:  " + jTablaCursos.getValueAt(jTablaCursos.getSelectedRow(), 1)
                + "\nCreditos:  " + jTablaCursos.getValueAt(jTablaCursos.getSelectedRow(), 2);
        JOptionPane.showMessageDialog(null, salida);
    }//GEN-LAST:event_jTablaCursosMouseClicked


