    /**
     * 
     * @param datosFilas
     * @param etiquetas 
     */
    public void setValores(String[][] datosFilas, String etiquetas[]) {
        if (datosFilas != null) {
            jTablaCursos.setModel(new DefaultTableModel(datosFilas, etiquetas));
            jScrollPane1.setViewportView(jTablaCursos);
        }
    }

    ///////////////////////////////////////////////////////////////////////////
    /**
     * 
     * @param controlador 
     */
    public void escucharTabla(MouseListener controlador) {
        jTablaCursos.addMouseListener(controlador);
    }

